    return (
       <React.Fragment>

       <div
                className={`tab-pane fade ${this.props.showActive}`}
                id={`v-pills-${this.props.currency}`}
                role='tabpanel'
                aria-labelledby={`v-pills-${this.props.currency}-tab`}
              >
              {this.state.bpi}
              </div>
    <div>
    </div>
  </React.Fragment>
    )
  }
}
